mapper.select:
select  PRODUCT_KEY, IMGMAIN1, PRODUCT_PRICE, PRODUCT_NAME, PRODUCT_ENROLL 
	from (select t1.*, rownum rn  
		from (SELECT  PRODUCT_KEY, IMGMAIN1, PRODUCT_PRICE , PRODUCT_NAME, PRODUCT_ENROLL FROM PRODUCT order by PRODUCT_KEY desc) t1 )
where rn between #{startRownum} and #{endRownum}

